CREATE TABLE "departements" (
	"id"	INTEGER NOT NULL UNIQUE,
	"departement"	TEXT NOT NULL,
	"approbe_par"	TEXT NOT NULL,
	"montant"	TEXT NOT NULL,
	"last_update"	REAL NOT NULL DEFAULT (datetime('now', 'localtime')),
	PRIMARY KEY("id" AUTOINCREMENT)
);

CREATE TABLE "liste_de_po" (
	"id"	INTEGER NOT NULL UNIQUE,
	"numero_de_po"	NUMERIC NOT NULL,
	"fournisseur"	TEXT NOT NULL,
	"demande_par"	TEXT,
	"description"	TEXT,
	"commentaire"	TEXT,
	"statut"		TEXT,
	"approbe_par"	TEXT,
	"last_update"	REAL NOT NULL DEFAULT (datetime('now', 'localtime')),
	FOREIGN KEY("approbe_par") REFERENCES "departements",
	PRIMARY KEY("id" AUTOINCREMENT)
);


CREATE VIEW administration AS SELECT * FROM departements WHERE departement='Administration';
CREATE VIEW bi AS SELECT * FROM departements WHERE departement='BI';
CREATE VIEW c_humain AS SELECT * FROM departements WHERE departement='Capital humain';
CREATE VIEW editorial AS SELECT * FROM departements WHERE departement='Éditorial';
CREATE VIEW finance AS SELECT * FROM departements WHERE departement='Finance';
CREATE VIEW information AS SELECT * FROM departements WHERE departement='Information';
CREATE VIEW nuglif AS SELECT * FROM departements WHERE departement='Nuglif';
CREATE VIEW p_numeriques AS SELECT * FROM departements WHERE departement='Produits Numériques';
CREATE VIEW publicite AS SELECT * FROM departements WHERE departement='Publicité';
CREATE VIEW ti AS SELECT * FROM departements WHERE departement='TI';
CREATE VIEW editeur AS SELECT * FROM departements WHERE departement='Éditeur';



CREATE VIEW liste_complet AS SELECT * FROM liste_de_po;